	def	get(id)
		@deseases[id]
	end

	def get_id(name)
		@deseases.each_index { |i|
			d = @deseases[i]
			if d[:name] == name then
				return i
			end
		}
		return nil
	end

	def list
		res = []
		@deseases.each { |d|
			next if d[:name] == 'Норма'
			res.push d[:name]
		}
		res
	end
